} 2. promise 有 3 种状态 test('promise 有 3 种状态', () => { const p1 = new _Promise() expect(p1.status).toBe('pending') const p2 = new _Promise(resolve => resolve()) expect(p2.status).toBe('fulfilled') const p3 = new _Promise((resolve, reject) => reject()) expect(p3.status).toBe('rejected')
}) // 新增 3 种状态
const STATUS_PENDING = 'pending'
const STATUS_FULFILLED = 'fulfilled'
const STATUS_REJECTED = 'rejected' class _Promise { constructor(executor = () => {}) { // 立即执行构造函数，并且状态变为 pending this.status = STATUS_PENDING const resolve = () => { // 执行 resolve 后，状态变为 fulfilled this.status = STATUS_FULFILLED } const reject = () => { // 执行 reject 后，状态变为 rejected this.status = STATUS_REJECTED } // 传入的回调会有两个参数 resolve/reject executor(resolve, reject) }
} 3. 执行 resolve、reject 后状态固化 test('执行 resolve、reject 后状态固化 ', () => { const p1 = new _Promise((resolve, reject) => { resolve() reject() }) expect(p1.status).toBe('fulfilled') const p2 = new _Promise((resolve, reject) => { reject() resolve() }) expect(p2.status).toBe('rejected')
}) const STATUS_PENDING = 'pending'
const STATUS_FULFILLED = 'fulfilled'
const STATUS_REJECTED = 'rejected' export class _Promise { constructor(executor = () => {}) { this.status = STATUS_PENDING const resolve = () => { // 如果状态时 PENDING 时才会执行代码 if (this.status === STATUS_PENDING) { this.status = STATUS_FULFILLED } } const reject = () => { // 如果状态时 PENDING 时才会执行代码 if (this.status === STATUS_PENDING) { this.status = STATUS_REJECTED } } executor(resolve, reject) }
}

