package utils import ( "errors" "fmt" "reflect"
) // 两个有公共字段的结构体互转
func StructConvert(src any, dst any) error { srcVal := reflect.ValueOf(src) dstVal := reflect.ValueOf(dst) if srcVal.Kind() != reflect.Ptr || dstVal.Kind() != reflect.Ptr { return errors.New("src和dst必须是指向结构体的指针") } srcVal = srcVal.Elem() dstVal = dstVal.Elem() srcType := srcVal.Type() dstType := dstVal.Type() if srcType.Kind() != reflect.Struct || dstType.Kind() != reflect.Struct { return errors.New("指针解引用后不是结构体类型") } for i := 0; i < srcVal.NumField(); i++ { srcField := srcVal.Field(i) srcFieldName := srcType.Field(i).Name dstField := dstVal.FieldByName(srcFieldName) if dstField.IsValid() && dstField.CanSet() && srcField.Type() == dstField.Type() { dstField.Set(srcField) } } fmt.Println(srcVal, dstVal) return nil
}

