CREATE OR REPLACE PROCEDURE p3( IN community_id CHAR(10)
)
AS
DECLARE male_count INT := 0; female_count INT := 0; gender CHAR(10); CURSOR cursor_student(community_id CHAR(10)) is SELECT s.性别 FROM 参加社团表 c JOIN 学生表 s ON c.学号 = s.学号 WHERE c.社团号 = community_id;
BEGIN -- 打开游标 OPEN cursor_student(community_id); -- 循环遍历游标结果集 LOOP FETCH cursor_student INTO gender; EXIT WHEN NOT FOUND; -- 计算男生和女生人数 IF gender = '男' THEN male_count := male_count + 1; ELSIF gender = '女' THEN female_count := female_count + 1; END IF; END LOOP; -- 关闭游标 CLOSE cursor_student; -- 显示结果 RAISE NOTICE '社团号: %, 男生人数: %, 女生人数: %', community_id, male_count, female_count;
END;
/ CALL p3('C003'); image-20240517202305051

